package writingcode;

public class printingalphabetpattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//static method dont need object reference
		//printing al
		
		int alpha = 65;
		
		for(int i=0;i<5;i++)
		{
		
				System.out.print((char)alpha + " " );
			
			alpha++;
			System.out.println();
		}
		
	}

}
